# This script will get a list of the alerts generated by the api
# These can be fed into other things like jira or some other shit

import requests
import json
ADDR = "localhost"
url = f"http://{ADDR}:5000/api/v1/scan/results"

data = requests.get(url).content
contents = json.loads(data)
for k, v in contents.items():
    if k == 'site':
        for site in v:
            if len(site['alerts']) > 1:
                alerts = site['alerts']
                # print(alerts) # Gets god damn everyhing

print(alerts)